mapper.select:
select *
from
(
select rownum as RNUM, B.*
from
(
select A.*
from member A

order by mem_No desc
)B
)
where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select * from ( select rownum as RNUM, B.* from ( select A.* from memExGrantView A
where
and
grant_flag = 'N' and type='일반회원' order by mem_No desc )B ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
select rownum as RNUM, B.*
from
(
select A.*
from product A

order by REGDATE desc
)B
)
where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select *
from
(
select rownum as RNUM, B.*
from
(
select A.*
from admin A

order by admin_No desc
)B
)
where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select * from ( select rownum as RNUM, B.* from ( select A.* from productFormView A
where
and
pay_flag='N' order by form_regdate desc )B ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
