mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from
    (
        select B.*, (sysdate-regdate)*24 as date_term
        from board B
        
        order by group_no desc, sort_no
    )A
)
where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
